mapper.select:
select
s_id, o_id, start_time, end_time, d_id, flag, status
from library.order


        and s_id = #{sId}


        and o_id = #{oId}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and d_id = #{dId}


        and flag = #{flag}


        and status = #{status}
